import React from 'react';

const Favorites = ({ favorites, removeFromFavorites }) => {
  return (
    <div>
      <h2>Favori Filmler</h2>
      {favorites.length === 0 ? (
        <p>Favorilere eklenmiş film yok.</p>
      ) : (
        <div>
          {favorites.map((movie) => (
            <div key={movie.imdbID} style={{ marginBottom: '10px' }}>
              <h3>{movie.Title}</h3>
              <img src={movie.Poster} alt={movie.Title} style={{ width: '150px' }} />
              <div>
                <button onClick={() => removeFromFavorites(movie.imdbID)}>
                  Favorilerden Çıkar
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Favorites;
