import React, { useState, useEffect } from 'react';

const MovieList = ({ movies, addToFavorites }) => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(savedFavorites);
  }, []);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const handleAddToFavorites = (movie) => {
    addToFavorites(movie);
  };

  return (
    <div>
      <h1>Film Listesi</h1>
      <div>
        {movies.map((movie, index) => (
          <div key={index}>
            <h3>{movie.Title}</h3>
            {movie.Poster && <img src={movie.Poster} alt={movie.Title} style={{ width: '100px' }} />}
            <button onClick={() => handleAddToFavorites(movie)}>Favorilere Ekle</button>
          </div>
        ))}
      </div>

      <h2>Favori Filmler</h2>
      <div>
        {favorites.length > 0 ? (
          favorites.map((fav, index) => (
            <div key={index}>
              <h4>{fav.Title}</h4>
              {fav.Poster && <img src={fav.Poster} alt={fav.Title} style={{ width: '100px' }} />}
            </div>
          ))
        ) : (
          <p>Henüz favori film eklenmedi.</p>
        )}
      </div>
    </div>
  );
};

export default MovieList;
