import React from 'react';
import './MovieList.css';

const MovieList = ({ movies, addToFavorites, removeFromFavorites, favorites }) => {
  return (
    <div>
      {movies.map(movie => (
        <div key={movie.imdbID} style={{ marginBottom: '20px' }}>
          <img src={movie.Poster} alt={movie.Title} />
          <h3>{movie.Title}</h3>
          {/* Eğer film favorilerdeyse "Favorilerden Çıkar" butonunu göster */}
          {favorites.some(fav => fav.imdbID === movie.imdbID) ? (
            <button className="remove-button" onClick={() => removeFromFavorites(movie.imdbID)}>
              Favorilerden Çıkar
            </button>
          ) : (
            <button className="add-button" onClick={() => addToFavorites(movie)}>
              Favorilere Ekle
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default MovieList;
