import React, { useState, useEffect } from 'react';
import MovieSearch from './components/MovieSearch';
import MovieList from './components/MovieList';
import MovieDetails from './components/MovieDetails';
import Favorites from './components/Favorites';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [favorites, setFavorites] = useState(
    JSON.parse(localStorage.getItem('favorites')) || []
  );

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const addToFavorites = (movie) => {
    setFavorites([...favorites, movie]);
  };

  const removeFromFavorites = (movie) => {
    setFavorites(favorites.filter(fav => fav.imdbID !== movie.imdbID));
  };

  return (
    <div>
      <MovieSearch onMoviesFetched={setMovies} />
      <MovieList movies={movies} onMovieSelect={setSelectedMovie} />
      <MovieDetails movie={selectedMovie} />
      <button onClick={() => addToFavorites(selectedMovie)}>Favorilere Ekle</button>
      <Favorites favorites={favorites} onRemoveFavorite={removeFromFavorites} />
    </div>
  );
};

export default App;
