import React from 'react';

const MovieList = ({ movies, addToFavorites }) => {
  return (
    <div>
      <h1>Film Listesi</h1>
      <div>
        {movies.length > 0 ? (
          movies.map((movie, index) => (
            <div key={index}>
              <h3>{movie.Title}</h3>
              {movie.Poster && <img src={movie.Poster} alt={movie.Title} style={{ width: '100px' }} />}
              <button onClick={() => addToFavorites(movie)}>Favorilere Ekle</button>
            </div>
          ))
        ) : (
          <p>Film bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default MovieList;
