// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import MovieSearch from './components/MovieSearch'; // Film arama bileşeni
import Favorites from './components/Favorites'; // Favoriler bileşeni
import { useState, useEffect } from 'react';

const App = () => {
  // Kullanıcı giriş yapmış mı kontrol etmek için bir state
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Yerel depolamada mevcut bir kullanıcı var mı kontrol et
    const storedUser = localStorage.getItem('authenticated');
    if (storedUser) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem('authenticated', 'true'); // Kullanıcıyı localStorage'a kaydet
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('authenticated'); // Kullanıcıyı localStorage'dan sil
  };

  return (
    <Router>
      <div>
        {isAuthenticated ? (
          <div>
            <button onClick={handleLogout}>Çıkış Yap</button>
            <h2>Hoşgeldiniz, film arama uygulamasına devam edin!</h2>
          </div>
        ) : (
          <h2>Lütfen giriş yapın veya kaydolun!</h2>
        )}
      </div>

      <Routes>
        <Route path="/" element={isAuthenticated ? <MovieSearch /> : <Login onLogin={handleLogin} />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/movies" element={isAuthenticated ? <MovieSearch /> : <Login onLogin={handleLogin} />} />
        <Route path="/favorites" element={isAuthenticated ? <Favorites /> : <Login onLogin={handleLogin} />} />
      </Routes>
    </Router>
  );
};

export default App;
