// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import MovieSearch from './components/MovieSearch';
import Favorites from './components/Favorites';
import { useState, useEffect } from 'react';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('authenticated');
    if (storedUser) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem('authenticated', 'true');
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('authenticated');
  };

  return (
    <Router>
      <div>
        {isAuthenticated ? (
          <div>
            <button onClick={handleLogout}>Çıkış Yap</button>
            <h2>Hoşgeldiniz! Film arama uygulamasına devam edin!</h2>
          </div>
        ) : (
          <h2>Lütfen giriş yapın veya kaydolun!</h2>
        )}
      </div>

      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/movies" /> : <Login onLogin={handleLogin} />}
        />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/movies"
          element={isAuthenticated ? <MovieSearch /> : <Navigate to="/login" />}
        />
        <Route
          path="/favorites"
          element={isAuthenticated ? <Favorites /> : <Navigate to="/login" />}
        />
      </Routes>
    </Router>
  );
};

export default App;
