import React, { useState, useEffect } from 'react';
import MovieList from './components/MovieList';
import axios from 'axios';

const App = () => {
  const [movies, setMovies] = useState([]); // Aranan filmleri tutuyor
  const [searchTerm, setSearchTerm] = useState(''); // Arama terimini tutuyor
  const [favorites, setFavorites] = useState([]); // Favori filmleri tutuyor

  // Sayfa yüklendiğinde favori filmleri localStorage'dan yükle
  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favorites'));
    if (savedFavorites) {
      setFavorites(savedFavorites);
    }
  }, []);

  // Filmleri OMDb API'den arayan fonksiyon
  const searchMovies = async () => {
    const response = await axios.get(`http://www.omdbapi.com/?s=${searchTerm}&apikey=f73519fa`);
    if (response.data.Search) {
      setMovies(response.data.Search);
    }
  };

  // Bir filmi favorilere ekleyen fonksiyon
  const addToFavorites = (movie) => {
    const newFavorites = [...favorites, movie];
    setFavorites(newFavorites);

    // Favori filmleri localStorage'a kaydet
    localStorage.setItem('favorites', JSON.stringify(newFavorites));
  };

  return (
    <div>
      <input 
        type="text" 
        value={searchTerm} 
        onChange={(e) => setSearchTerm(e.target.value)} 
        placeholder="Film Ara" 
      />
      <button onClick={searchMovies}>Ara</button>

      {/* MovieList bileşenine favorilere ekleme fonksiyonunu aktar */}
      <MovieList movies={movies} addToFavorites={addToFavorites} />
    </div>
  );
};

export default App;
