import React, { useState } from 'react';

const MovieList = ({ movies, addToFavorites }) => {
  const [addedToFavorites, setAddedToFavorites] = useState({}); // Favorilere eklenen filmleri takip eden state

  const handleAddToFavorites = (movie) => {
    addToFavorites(movie);
    setAddedToFavorites((prevState) => ({
      ...prevState,
      [movie.imdbID]: true, // Eklenen filmin imdbID'sini true olarak işaretle
    }));
  };

  return (
    <div>
      <h1>Film Listesi</h1>
      <div>
        {movies.length > 0 ? (
          movies.map((movie, index) => (
            <div key={index} style={{ marginBottom: '20px' }}>
              <h3>{movie.Title}</h3>
              {movie.Poster && <img src={movie.Poster} alt={movie.Title} style={{ width: '100px' }} />}
              <button 
                onClick={() => handleAddToFavorites(movie)} 
                disabled={addedToFavorites[movie.imdbID]} // Eğer eklendiyse buton pasif olur
              >
                {addedToFavorites[movie.imdbID] ? 'Favorilere Eklendi' : 'Favorilere Ekle'}
              </button>
            </div>
          ))
        ) : (
          <p>Film bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default MovieList;
