// src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MovieList from './components/MovieList';
import Favorites from './components/Favorites';
import Login from './components/Login';
import Register from './components/Register';
import axios from 'axios';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null); // Kullanıcı durumunu yönet

  const searchMovies = async () => {
    const response = await axios.get(`http://www.omdbapi.com/?s=${searchTerm}&apikey=f73519fa`);
    if (response.data.Search) {
      setMovies(response.data.Search);
    }
  };

  const addToFavorites = (movie) => {
    if (!favorites.some(fav => fav.imdbID === movie.imdbID)) {
      const updatedFavorites = [...favorites, movie];
      setFavorites(updatedFavorites);
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    }
  };

  const handleLogin = (email) => {
    // Giriş işlemini burada gerçekleştirin
    setUser(email);
  };

  const handleRegister = (email) => {
    // Kayıt işlemini burada gerçekleştirin
    setUser(email);
  };

  return (
    <Router>
      <div>
        <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '10px', backgroundColor: '#282c34', color: 'white' }}>
          <h2>Film Arama Uygulaması</h2>
          {user ? (
            <div>Hoşgeldin, {user} <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>Çıkış Yap</Link></div>
          ) : (
            <div>
              <Link to="/login" style={{ color: 'white', textDecoration: 'none' }}>Giriş Yap</Link>
              <Link to="/register" style={{ color: 'white', textDecoration: 'none', marginLeft: '10px' }}>Kayıt Ol</Link>
            </div>
          )}
          <Link to="/favorites" style={{ color: 'white', textDecoration: 'none' }}>
            <div style={{ cursor: 'pointer' }}>
              Favoriler ({favorites.length})
            </div>
          </Link>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <input 
                  type="text" 
                  value={searchTerm} 
                  onChange={(e) => setSearchTerm(e.target.value)} 
                  placeholder="Film Ara"
                />
                <button onClick={searchMovies}>Ara</button>
                <MovieList movies={movies} addToFavorites={addToFavorites} />
              </div>
            }
          />
          <Route
            path="/favorites"
            element={<Favorites favorites={favorites} />}
          />
          <Route
            path="/login"
            element={<Login onLogin={handleLogin} />}
          />
          <Route
            path="/register"
            element={<Register onRegister={handleRegister} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
